import React, { useState } from "react";
import './index.css'
const App = () => {
    const [fullname, setFullname] = useState({
        fname: "",
        lname: "",
        email: ""
    });
    const OnSubmit = (event) => {
        event.preventDefault();
        alert("Form submitted");
    };
    const inputEvent = (event) => {
        // console.log(event.target.value);
        // console.log(event.target.name);
        // const value = event.target.value;
        // const name = event.target.name;

        const {value,name}=event.target; //Object destructuring

        setFullname((prevalue) => {
            //  console.log(prevalue);
            if (name === 'fname') {
                return{
                    fname: value,
                    lname : prevalue.lname,
                    email : prevalue.email,
                    phno : prevalue.phno
                }
            }else if(name === 'lname'){
                return{
                    fname: prevalue.fname,
                    lname :value,
                    email : prevalue.email,
                    phno : prevalue.phno
                }
            }
            else if(name==='email'){
                return{
                    fname: prevalue.fname,
                    lname : prevalue.lname,
                    email : value,
                    phno : prevalue.phno
                }
            }else if(name === 'phno'){
                return{
                    fname: prevalue.fname,
                    lname : prevalue.lname,
                    email : prevalue.email,
                    phno : value
                }

            }

        });
    };
    return (
        <>
            <div className="main_div">
                <form onSubmit={OnSubmit}>
                    <div>
                        <h1>Hello {fullname.fname}{fullname.lname}}</h1>
                        <input type="text" placeholder="Enter your name" name="fname" onChange={inputEvent}
                        value={fullname.fname}
                        />
                        <br />
                        <input type="text" placeholder="Enter your lastname" name="lname" onChange={inputEvent}
                        value={fullname.lname}
                        />
                        <br />
                        <input type="email" placeholder="Enter your email" name="email" onChange={inputEvent}
                            value={fullname.email}
                        />
                        <input type="number" placeholder="Enter your Phone number" name="phno" onChange={inputEvent}
                            value={fullname.phno}
                        />
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        </>
    );
}
export default App
