import React, { useState } from "react";
import './index.css'
const App = () => {
    const [name,setname]=useState(()=>{
        fname:'' ,
        lname

    });

    
   
    const OnSubmit=(event)=>{
        event.preventDefault();
        alert("Form submitted");
        
        
    };
    const inputEvent=(event)=>{
        // console.log(event.target.value);
        setname(event.target.value);
    };
    
    return (
        <>
        <div className="main_div">

        <form onSubmit={OnSubmit}>

        <div>
          <h1>Hello </h1>   
          <input type="text" placeholder="Enter your name" name="fname" onChange={inputEvent} 
            value={name}
          />
          <br/>
          <input type="text" placeholder="Enter your lastname" name="lname" onChange={inputEvent} 
            value={lname}
          />
           <br/>
          <input type="text" placeholder="Enter your lastname" name="email" onChange={inputEvent} 
            value={email}
          />
          <button type="submit">Submit</button>
        </div>
        </form>
        </div>
        
        </>
    );
}
export default App
