import React, { useState } from "react";
import './index.css'


const App = () => {

    const purple="#8e44ad";
    let dbgChange="#8e44ad";

    const [bg,setBg]=useState(purple);
    const [name,setname]=useState("click me!!")
    

const bgChange=()=>{
   // console.log("Clicked");
   let newBg="#34495e";
   setBg(newBg);
   setname('ouch 🦸‍♀️')
};
const DbgChange=()=>{
  
    setBg(purple);
    setname("Boom :cra")
   

};


    return (
        <>
        <div style={{backgroundColor:bg}}>

    <button onClick={bgChange} onDoubleClick={DbgChange}>{name}</button>

        </div>
        </>
    )
}
export default App
