            //         lname : prevalue.lname,
            //         email : prevalue.email,
            //         phno : value
            //     }

            // }

        });
    };
    return (
        <>
            <div className="main_div">
                <form onSubmit={OnSubmit}>
                    <div>
                        <h1>Hello {fullname.fname}{fullname.lname}</h1>
                        <p>{fullname.email}</p>
                        <p>{fullname.phno}</p>
                        <input type="text" placeholder="Enter your name" name="fname" onChange={inputEvent}
                        value={fullname.fname}
                        />
                        <br />
                        <input type="text" placeholder="Enter your lastname" name="lname" onChange={inputEvent}
                        value={fullname.lname}
                        />
                        <br />
                        <input type="email" placeholder="Enter your email" name="email" onChange={inputEvent}
                            value={fullname.email}
                        />
                        <input type="number" placeholder="Enter your Phone number" name="phno" onChange={inputEvent}
                            value={fullname.phno}
                        />
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        </>
    );
}
export default App
